import React from "react";
// reactstrap components
import {
  UncontrolledCollapse,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  NavbarBrand,
  Navbar,
  NavItem,
  NavLink,
  Nav,
  Container,
  Row,
  Col,
} from "reactstrap";



const items = [
  {
    src: require("assets/img/Nahata-Sport.png").default,

  },
  {
    src: require("assets/img/Nahata-Sport2.png").default,

  },
  {
    src: require("assets/img/Nahata-Sport.png").default,

  },
];

function Happyhours() {

  const [activeIndex, setActiveIndex] = React.useState(0);
  const [animating, setAnimating] = React.useState(false);
  const onExiting = () => {
    setAnimating(true);
  };
  const onExited = () => {
    setAnimating(false);
  };
  const next = () => {
    if (animating) return;
    const nextIndex = activeIndex === items.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };
  const previous = () => {
    if (animating) return;
    const nextIndex = activeIndex === 0 ? items.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };
  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };

  return (
    <>
      <div className="happymainsec">
        <Container>
            <div className="row mainheading">
              <div className="col md-4"><hr className="home-border"/></div>
              <div  className=" col md-4 home1"> <h6 className="home"><span> Happy Hours </span></h6></div>
              <div className="col md-4" ><hr className="home-border"/></div>
            </div>
            </Container>
          <Container className="happycard" >
            <Row xs="1" sm="2" md="3">
              <Col> <img
                  alt="..."
                  className="img img-responsive"
                  src={require("assets/img/Happy1.png").default}
                  style={{ width: "100%" }}
                />
                </Col>
              <Col><img
                  alt="..."
                  className="img img-responsive"
                  src={require("assets/img/Happy3.png").default}
                  style={{ width: "100%" }}
                />
                </Col>
              <Col><img
                  alt="..."
                  className="img img-responsive"
                  src={require("assets/img/Happy1.png").default}
                  style={{ width: "100%" }}
                />
                </Col> 
            </Row>

          </Container>
      </div>
    </>
  );
}

export default Happyhours;
